import React, {useState} from 'react';

const Contact = (proop) => {
  const [contacts, setContacts] = useState(proop.contacts);
  const res = [];

  contacts.forEach((e, i) => {
    res.push(<div key={i}><hr/><p>{e.firstName} {e.lastName} {e.phone}</p></div>)
  });

  const handleSearchChange = event => {
    // За ім'ям
    if(proop.contacts.filter(e => e.firstName.toUpperCase().includes(event.target.value.toUpperCase())).length != 0)
      setContacts(proop.contacts.filter(e => e.firstName.toUpperCase().includes(event.target.value.toUpperCase())));
    // За прізвищем
    if(proop.contacts.filter(e => e.lastName.toUpperCase().includes(event.target.value.toUpperCase())).length != 0)
      setContacts(proop.contacts.filter(e => e.lastName.toUpperCase().includes(event.target.value.toUpperCase())));
    // За телефоном
    if(proop.contacts.filter(e => e.phone.includes(event.target.value)).length != 0)
      setContacts(proop.contacts.filter(e => e.phone.includes(event.target.value)));
  }

  return (
    <div>
      <input placeholder='type' onChange={event => handleSearchChange(event)} />
      <div>{res}</div>
    </div>
  );
}

export default Contact;
