import React, {Fragment, useState} from 'react';
import API from './contacts.json';

const Contact = (proop) => {
  const [contacts, setContacts] = useState(API);
  const res = [];

  contacts.forEach(e => {
    res.push(<div className='contact'><p className='name'>{e.firstName} {e.lastName}<p className='phone'>{e.phone}</p></p></div>)
  });

  const handleSearchChange = event => {
    // За ім'ям
    if(API.filter(e => e.firstName.toUpperCase().includes(event.target.value.toUpperCase())).length !== 0)
      setContacts(API.filter(e => e.firstName.toUpperCase().includes(event.target.value.toUpperCase())));
    // За прізвищем
    else if(API.filter(e => e.lastName.toUpperCase() == event.target.value.toUpperCase()).length !== 0)
      setContacts(API.filter(e => e.lastName.toUpperCase() == event.target.value.toUpperCase()));
    // За телефоном
    else if(API.filter(e => e.phone == event.target.value).length !== 0)
      setContacts(API.filter(e => e.phone == event.target.value));
    
    // CheckButton
    console.log(contacts[0].gender);
    if (document.querySelector('.checkBox').innerHTML === 'Ч')
      setContacts(contacts.filter((e) => e.gender == 'male'));
    console.log(contacts);
  }

  const c = check();
  function* check() {
    while (true) {
      yield 'Ч'; // Чоловіки
      yield 'Ж'; // Жінки
      yield '--'; // Не вказано
      yield 'В'; // Всі
    }
  }
  const checkBox = () => document.querySelector('.checkBox').innerHTML = c.next().value;

  return (
    <Fragment>
      <header>
        <p className='title'>Контакти</p>
        <input placeholder='Ввести' onChange={event => handleSearchChange(event)} />
        <p onClick={() => checkBox()} className='checkBox'>В</p>
      </header>
      <div>{res}</div>
    </Fragment>
  );
}

export default Contact;
