import React, {useState} from 'react';
import API from './contacts.json';

const Contact = (proop) => {
  const [contacts, setContacts] = useState(API);
  const res = [];

  contacts.forEach((e, i) => {
    res.push(<div key={i}><hr/><p>{e.firstName} {e.lastName} {e.phone}</p></div>)
  });

  const handleSearchChange = event => {
    // За ім'ям
    if(API.filter(e => e.firstName.toUpperCase().includes(event.target.value.toUpperCase())).length !== 0)
      setContacts(API.filter(e => e.firstName.toUpperCase().includes(event.target.value.toUpperCase())));
    // За прізвищем
    if(API.filter(e => e.lastName.toUpperCase().includes(event.target.value.toUpperCase())).length !== 0)
      setContacts(API.filter(e => e.lastName.toUpperCase().includes(event.target.value.toUpperCase())));
    // За телефоном
    if(API.filter(e => e.phone.includes(event.target.value)).length !== 0)
      setContacts(API.filter(e => e.phone.includes(event.target.value)));
  }

  function* check(m) {
    yield m
    
  }

  const checkBox = () => {
    const t = check(2).next();
    console.log(t);
  }

  return (
    <div>
      <input placeholder='type' onChange={event => handleSearchChange(event)} />
      <h4 onClick={() => checkBox()}>A</h4>
      <div>{res}</div>
    </div>
  );
}

export default Contact;
