import React, {useState} from 'react';

const Contact = (proop) => {
  const [contacts, setContacts] = useState(proop.contacts);
  const [search, setSearch] = useState('');

  const res = [];

  contacts.forEach((e, i) => {
    res.push(<div key={i}><hr/><p>{e.firstName} {e.lastName} {e.phone}</p></div>)
  });

  const handleSearchChange = event => {
    console.log(event.target.value);
    console.log(search);
    if (event.target.value == '') setSearch('');
    else {}
    setSearch(event.target.value);
    setContacts(proop.contacts.filter(e => e.firstName.toUpperCase().includes(search.toUpperCase())));
    // setContacts(proop.contacts.filter(e => e.lastName.toUpperCase().includes(search.toUpperCase())));
    // setContacts(proop.contacts.filter(e => e.phone.toUpperCase().includes(search.toUpperCase())));
  }

  return (
    <div>
      <input placeholder='type' value={search} onChange={event => handleSearchChange(event)} />
      <div>{res}</div>
    </div>
  );
}

export default Contact;
