import React, {useState} from 'react';
import ReactDOM from 'react-dom/client';

const Contact = (proop) => {
  console.log(proop.contacts);

  const [contacts, setContacts] = useState(proop.contacts);
  const [search, setSearch] = useState('');
  const res = [];

  contacts.forEach((e, i) => {
    res.push(<div key={i}><hr/><p>{e.firstName}</p></div>)
  });

  const handleSearchChange = event => {
    setSearch(event.target.value);
    setContacts(proop.contacts.filter(e => e.lastName.include(search)))
  }

  return (
    <Fragment>
      <input placeholder='type' value={search} onChange={event => handleSearchChange(event)} />
      <div>{res}</div>
    </Fragment>
  );
}

export default Contact;
