import React, {useState} from 'react';

const Contact = (proop) => {
  const [contacts, setContacts] = useState(proop.contacts);

  const res = [];

  contacts.forEach((e, i) => {
    res.push(<div key={i}><hr/><p>{e.firstName} {e.lastName} {e.phone}</p></div>)
  });

  const handleSearchChange = event => {
    console.log(event.target.value);
    console.log(contacts);
    setContacts(proop.contacts.filter(e => (e.firstName, e.lastName, e.p).toUpperCase().includes(event.target.value.toUpperCase())));
    // setContacts(proop.contacts.filter(e => e.lastName.toUpperCase().includes(event.target.value.toUpperCase())));
    // setContacts(proop.contacts.filter(e => e.phone.toUpperCase().includes(event.target.value.toUpperCase())));
  }

  return (
    <div>
      <input placeholder='type' onChange={event => handleSearchChange(event)} />
      <div>{res}</div>
    </div>
  );
}

export default Contact;
